            {"Drug":"Heparin ","Score":0.361411},{"Drug":"Ethinylestradiol ","Score":0.361411},
            {"Drug":"Hydrogen peroxide ","Score":0.331263},{"Drug":"Folic acid ","Score":0.318921},
            {"Drug":"Nicotine ","Score":0.289115},{"Drug":"Dalteparin ","Score":0.289115},
            {"Drug":"Chromium ","Score":0.266055},{"Drug":"Troglitazone ","Score":0.265562},
            {"Drug":"Alitretinoin ","Score":0.265562},{"Drug":"Bexarotene ","Score":0.265562},
            {"Drug":"Formaldehyde ","Score":0.211013},{"Drug":"Phenytoin ","Score":0.211013},
            {"Drug":"Arsenic trioxide ","Score":0.211013},{"Drug":"Copper ","Score":0.211013},
            {"Drug":"Estriol ","Score":0.211013},{"Drug":"Chorionic Gonadotropin (Human) ","Score":0.211013},
            {"Drug":"Urea ","Score":0.211013},{"Drug":"Belinostat ","Score":0.211013},
            {"Drug":"Phenol ","Score":0.211013},{"Drug":"Ursodeoxycholic acid ","Score":0.193759},
            {"Drug":"Fenofibrate ","Score":0.193759},{"Drug":"Vitamin E ","Score":0.193759},
            {"Drug":"Arachidonic Acid ","Score":0.193759},{"Drug":"Ciprofibrate ","Score":0.193759},
            {"Drug":"Nitroaspirin ","Score":0.193759},{"Drug":"Ascorbic acid ","Score":0.193759},
            {"Drug":"Acetylsalicylic acid ","Score":0.193759},{"Drug":"Niacin ","Score":0.193759},
            {"Drug":"Nandrolone decanoate ","Score":0.193759},{"Drug":"beta-Naphthoflavone ","Score":0.155136},
            {"Drug":"Progesterone ","Score":0.155136},{"Drug":"Cisplatin ","Score":0.150344},
            {"Drug":"Selenium ","Score":0.078004},{"Drug":"Phenobarbital ","Score":0.078004},
            {"Drug":"Norethisterone ","Score":0.072296},{"Drug":"Aminocaproic acid ","Score":0.072296},
            {"Drug":"Prednisone ","Score":0.072296},{"Drug":"Antimony ","Score":0.072296}],
            pesquisas: [],
            loading: false,
            change: 0
        };
    }
    async componentDidMount() {
        const token = localStorage.getItem('token');
        api.defaults.headers.Authorization = `Bearer ${token}`;
        const id = localStorage.getItem('id');
        this.setState({ token, id});
        try {
            const response = await api.get(`/pesquisas/`);
            this.setState({pesquisas: response.data });
        }catch (error) {
            toast.error('Erro na comunicação com o servidor');
        }
    }
    async componentDidUpdate(_, prevState) {
        if(prevState.change !== this.state.change) {
            try {
                const response = await api.get(`/pesquisas`);
                this.setState({pesquisas: response.data });
            }catch (error) {
                toast.error('Erro na comunicação com o servidor');
            }
        }
    }
    handleInputChange = e => {
        this.setState({ proteina: e.target.value });
    };
    handleSubmit = async e => {
        e.preventDefault();
        this.setState({ loading: true });
        const data = new FormData();
        data.append('proteina', this.state.proteina);
        try {
            await api.post(`/pesquisas/`, data);
            this.setState({ change: this.state.change+1, proteina: '' });
            toast.success('Pesquisa realizada com sucesso!');
        } catch (error) {
            toast.error('Falha ao realizar pesquisa!');
        }
        this.setState({ loading: false });
    };
    remover = async id => {
        this.setState({ loading: true });
        try {
            await api.delete(`/pesquisas/${id}`);
            this.setState({ change: this.state.change+1, proteina: ''});
            toast.success('Pesquisa apagado com sucesso!');
        } catch (error){
            toast.error('Falha ao remover pesquisa!');
        }
        this.setState({ loading: false });
    }
    render() {
        const { id, proteina, pesquisas, loading } = this.state;
        return (
            <Content>
                <Form onSubmit={this.handleSubmit} method="POST">
                    <textarea placeholder="Pesquisar" name="proteina" required
                    onChange={this.handleInputChange} value={proteina}></textarea>
                    <aside>
                        <button type="submit">{loading ? 'Carregando...' : 'Pesquisar'}</button>
                    </aside>
                </Form>

                <List>
                    {pesquisas.map(pesquisa => (
                        <li key={pesquisa.id}>
                            <div>
                                {parseInt(id) === pesquisa.user.id &&
                                    <span>
                                        <button id="remover" type="button" onClick={() => this.remover(pesquisa.id)}>
                                            <FaTrashAlt />
                                        </button>
                                    </span>
                                }
                                <Moment format="DD/MM/YYYY HH:mm:ss">{pesquisa.created_at}</Moment>
                            </div>
                            <div>
                                <h4>Proteína:</h4>
                                {pesquisa.proteina}
                            </div>
                        </li>
                    ))}
                </List>
            </Content>
        );
    }
}
