import React, { Component } from 'react';
import api from '../../services/api';
import { toast } from 'react-toastify';
import Moment from 'react-moment';
import { FaTrashAlt } from 'react-icons/fa';
import { Content, Form, List } from './styles';
export default class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            token: '',
            id: '',
            content: '',
            tweets: [],
            loading: false,
            change: 0
        };
    }
    async componentDidMount() {
        const token = localStorage.getItem('token');
        api.defaults.headers.Authorization = `Bearer ${token}`;
        const id = localStorage.getItem('id');
        this.setState({ token, id});
        try {
            const response = await api.get(`/tweets/`);
            this.setState({tweets: response.data });
        }catch (error) {
            toast.error('Erro na comunicação com o servidor');
        }
    }
    async componentDidUpdate(_, prevState) {
        if(prevState.change !== this.state.change) {
            try {
                const response = await api.get(`/tweets`);
                this.setState({tweets: response.data });
            }catch (error) {
                toast.error('Erro na comunicação com o servidor');
            }
        }
    }
    handleInputChange = e => {
        this.setState({ content: e.target.value });
    };
    handleSubmit = async e => {
        e.preventDefault();
        this.setState({ loading: true });
        const data = new FormData();
        data.append('content', this.state.content);
        try {
            await api.post(`/tweets/`, data);
            this.setState({ change: this.state.change+1, content: '' });
            toast.success('Pesquisa realizado com sucesso!');
        } catch (error) {
            toast.error('Falha ao realizar pesquisa!');
        }
        this.setState({ loading: false });
    };
    remover = async id => {
        this.setState({ loading: true });
        try {
            await api.delete(`/tweets/${id}`);
            this.setState({ change: this.state.change+1, content: ''});
            toast.success('Pesquisa apagado com sucesso!');
        } catch (error){
            toast.error('Falha ao remover pesquisa!');
        }
        this.setState({ loading: false });
    }
    render() {
        const { id, content, tweets, loading } = this.state;
        return (
            <Content>
                <Form onSubmit={this.handleSubmit} method="POST">
                    <textarea placeholder="Enviar tweet" name="content" required
                    onChange={this.handleInputChange} value={content}></textarea>
                    <aside>
                        <button type="submit">{loading ? 'Carregando...' : 'Pesquisar'}</button>
                    </aside>
                </Form>
                <List>
                    {tweets.map(tweet => (
                        <li key={tweet.id}>
                            <div>
                                {parseInt(id) === tweet.user.id &&
                                    <span>
                                        <button type="button" onClick={() => this.remover(tweet.id)}>
                                            <FaTrashAlt />
                                        </button>
                                    </span>
                                }
                                <Moment format="DD/MM/YYYY HH:mm:ss">{tweet.created_at}</Moment>
                            </div>
                            <div>{tweet.proteina}</div>
                        </li>
                    ))}
                </List>
            </Content>
        );
    }
}
